$navbarDefault=$(".navbar-default");$navbarBrand=$navbarDefault.find(".navbar-brand");$navbarLinks=$navbarDefault.find(".navbar-nav > li > a");themeColor=$(".cover-content h1").css("color");$(window).on("load scroll resize",function(){if($(document).scrollTop()<50){$navbarDefault.css("background-color","transparent");$navbarBrand.css("color","white");$navbarLinks.css("color",themeColor)}else{$navbarDefault.css("background-color",themeColor);$navbarBrand.css("color","inherit");$navbarLinks.css("color","inherit")}});$("a.smooth-scroll").bind("click",function(b){var a=$(this);$("html, body").stop().animate({scrollTop:$(a.attr("href")).offset().top-150},500);b.preventDefault()});